    <!-- Váltó gombok -->
    <button onclick="moveCard(-1)" class="absolute left-0 text-white bg-black/50 px-4 py-2 rounded-full">←</button>
    <button onclick="moveCard(1)" class="absolute right-0 text-white bg-black/50 px-4 py-2 rounded-full">→</button>
